import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SvgIcon from '@material-ui/core/SvgIcon';

import SearchBar from '../components/SearchBar'

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    textAlign: 'center',
    marginLeft: 'auto',
    marginRight: 'auto',
    marginTop: 150,
  },
  homeIcon: {
    margin: theme.spacing(2),
    position: 'absolute',
    top: 0,
    right: 100,
    '&:hover': {
      color: '#BDBDBD',
      cursor: 'pointer',
    },
  },
  cartIcon: {
    margin: theme.spacing(2),
    position: 'absolute',
    top: 0,
    right: 50,
    '&:hover': {
      color: '#BDBDBD',
      cursor: 'pointer',
    },
  },
  accountIcon: {
    margin: theme.spacing(2),
    position: 'absolute',
    top: 0,
    right: 0,
    '&:hover': {
      color: '#BDBDBD',
      cursor: 'pointer',
    },
  },
  heroHeader: {
    fontSize: 70,
  },
  heroCaption: {
    color: '#BDBDBD',
    top: -90,
  },
  searchBar: {
    marginTop: 50,
  }
}));

function HomeIcon(props) {
  return (
    <SvgIcon {...props}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/></svg>
    </SvgIcon>
  );
}

function CartIcon(props) {
  return (
    <SvgIcon {...props}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M7 18c-1.1 0-1.99.9-1.99 2S5.9 22 7 22s2-.9 2-2-.9-2-2-2zM1 2v2h2l3.6 7.59-1.35 2.45c-.16.28-.25.61-.25.96 0 1.1.9 2 2 2h12v-2H7.42c-.14 0-.25-.11-.25-.25l.03-.12.9-1.63h7.45c.75 0 1.41-.41 1.75-1.03l3.58-6.49c.08-.14.12-.31.12-.48 0-.55-.45-1-1-1H5.21l-.94-2H1zm16 16c-1.1 0-1.99.9-1.99 2s.89 2 1.99 2 2-.9 2-2-.9-2-2-2z"/></svg>
    </SvgIcon>
  );
}

function AccountIcon(props) {
  return (
    <SvgIcon {...props}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 18 18"><path d="M9 1C4.58 1 1 4.58 1 9s3.58 8 8 8 8-3.58 8-8-3.58-8-8-8zm0 2.75c1.24 0 2.25 1.01 2.25 2.25S10.24 8.25 9 8.25 6.75 7.24 6.75 6 7.76 3.75 9 3.75zM9 14.5c-1.86 0-3.49-.92-4.49-2.33C4.62 10.72 7.53 10 9 10c1.47 0 4.38.72 4.49 2.17-1 1.41-2.63 2.33-4.49 2.33z"/></svg>
    </SvgIcon>
  );
}

function Home() {
  const classes = useStyles();

  return (
    <div>
      <HomeIcon className={classes.homeIcon} />
      <CartIcon className={classes.cartIcon} />
      <AccountIcon className={classes.accountIcon} />
      <div className={classes.root}>
        <h1 className={classes.heroHeader}>E-Com</h1>
        <p className={classes.heroCaption}>An e-commerce site template made with <strong>React.js</strong> and <strong>Material-UI</strong>.</p>
        <SearchBar className={classes.searchBar} />
      </div>
    </div>
  );
}

export default Home;
