import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SvgIcon from '@material-ui/core/SvgIcon';

import SearchBar from '../components/SearchBar';
import MarketItems from '../components/MarketItems';
import CartNavPopper from '../components/CartNavPopper';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    textAlign: 'center',
    marginLeft: 'auto',
    marginRight: 'auto',
    marginTop: 150,
  },
  homeIcon: {
    margin: theme.spacing(2),
    position: 'absolute',
    top: 0,
    right: 100,
    '&:hover': {
      color: '#BDBDBD',
      cursor: 'pointer',
    },
  },
  accountIcon: {
    margin: theme.spacing(2),
    position: 'absolute',
    top: 0,
    right: 0,
    '&:hover': {
      color: '#BDBDBD',
      cursor: 'pointer',
    },
  },
  heroHeader: {
    fontSize: 70,
    cursor: 'pointer',
  },
  heroCaption: {
    color: '#BDBDBD',
  },
  heroImage: {
    position: 'relative'
  }
}));

function HomeIcon(props) {
  return (
    <SvgIcon {...props}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/></svg>
    </SvgIcon>
  );
}

function AccountIcon(props) {
  return (
    <SvgIcon {...props}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 18 18"><path d="M9 1C4.58 1 1 4.58 1 9s3.58 8 8 8 8-3.58 8-8-3.58-8-8-8zm0 2.75c1.24 0 2.25 1.01 2.25 2.25S10.24 8.25 9 8.25 6.75 7.24 6.75 6 7.76 3.75 9 3.75zM9 14.5c-1.86 0-3.49-.92-4.49-2.33C4.62 10.72 7.53 10 9 10c1.47 0 4.38.72 4.49 2.17-1 1.41-2.63 2.33-4.49 2.33z"/></svg>
    </SvgIcon>
  );
}

function Home() {
  const classes = useStyles();

  return (
    <div>
      <HomeIcon className={classes.homeIcon} />
      <CartNavPopper />
      <AccountIcon className={classes.accountIcon} />
      <div className={classes.root}>
        <img className={classes.heroImage} width={75} height={100} src='https://i.pinimg.com/originals/1e/27/09/1e2709b9ab3be00ada0bb38e3b723bb9.png'></img>
        <h1 className={classes.heroHeader}>Kahve Mağazası</h1>
        <p className={classes.heroCaption}><strong style={{color: '#29B6F6'}}>React.js</strong> & <strong style={{color: '#FF7043'}}>MaterialUI</strong> ile yapılmış bir e-ticaret sitesi teması.</p>
        <SearchBar />
      </div>
      <MarketItems />
      <footer className={classes.footer}>
        <p style={{
          textAlign: 'center',
          padding: 50,
          marginTop: 150,
          backgroundColor: '#FAFAFA',
          color: '#212121',
        }}>Made with <span style={{color: '#FF7043'}}>&hearts;</span> by <span style={{color: '#9E9E9E', cursor: 'pointer'}}>gokhang.</span></p>
      </footer>
    </div>
  );
}

export default Home;
